import React, { useEffect, useState } from 'react'
import './banner.css'
import axios from '../../Static/axios'
import { API_KEY } from '../../Static/static'
import { img_url } from '../../Static/static'
import YouTube from 'react-youtube'
function Banner() {
    const [movie, setMovie] = useState()
    const [url, setUrl] = useState("")
      useEffect(() => {
              axios.get(`/trending/all/day?api_key=${API_KEY}`).then((response)=>{
                  // console.log(response.data.results[1])
                  setMovie(response.data.results[1
                  ])
              })
      },[])
      
      const getMovieid=(id)=>{
        console.log(id)
        console.log("clicked")
        axios.get(`/movie/${id}/videos?api_key=${API_KEY}&language=en-US`).then((response)=>{
          console.log(response.data.results[0].key)
          setUrl(response.data.results[0].key)
      
    
        
        })
    
      }
      
      const opts = {
        height: '390',
        width: '100%',
        playerVars: {
          // https://developers.google.com/youtube/player_parameters
          autoplay: 1,
        },
      };
    

  return (
    <div className='banner'
    style={{backgroundImage:`url(${ movie ? img_url+movie.backdrop_path:""})`}}
    >
    {url && <YouTube videoId={url} opts={opts}/>}
      <div className='content'>
      <h1 className='title'> {movie ? movie.title: ""}</h1>
         <div className='Banner_button'>
               <button className='Button' onClick={()=>getMovieid()}>Play</button>
               <button className='Button'>MyList</button>
          </div>
          <h1 className='discription'>{movie ? movie.overview : ""}</h1>

      
      </div>
    </div>
  )
}

export default Banner
